window.onload = function () {

    var
        body = $("body"),
        active = "active",
        btnRubrics = $(".btn_rubrics"),

        fildRubrics = $(".bl_rubrics"),
        btnSearch = $(".btn_search"),
        searchFild = $(".bl_search");


    ///  sliderEffect
    function sliderEffectForButtons(button, element, duration) {
        button.click(function () {
            element.slideToggle(duration);
        });
    }


    /// toggle class Active
    function toggleClassActive(btn, fild) {
        btn.on("click", function () {
            fild.toggleClass(active);
        });
    }

    //  hideShowEffect
    function hideShowEffect(button, element) {
        button.click(function () {
            element.toggleClass("hidden");
        });
    }

    // LikeLinks buttons
    function likeLink(btn) {
        btn.on("click", function () {
            var urlLink = $(this).attr("data-href");
            if (urlLink === "") {
                alert("URL address isn't correct / шляпа");
            } else {
                window.open(urlLink);
            }
        })
    }

    likeLink($(".sliderBlock__link"));
    likeLink($(".btn_bonus__getBonus"));





    var btnSymbols = $(".btn_moreSymbolInfo"),
        symbolsItem = $(".game_symbol__wrapper");

    sliderEffectForButtons(btnSymbols, symbolsItem, 500);


    var btnBonusInfo = $(".btn_bonus__info");

    btnBonusInfo.on("click", function () {
        $(this).parent(".bl_bonus").toggleClass("bl_bonus__active");
    });


    if (body.width() <= 991) {

        sliderEffectForButtons(btnRubrics, fildRubrics, 500);
        toggleClassActive(btnSearch, searchFild);

    }





    $('.bl_topGames').slick({
        dots: false,
        infinite: true,

        autoplay: true,
        autoplaySpeed: 1500,

        centerMode: true,
        speed: 1000,
        slidesToShow: 3,
        slidesToScroll: 1,
        responsive: [
            {
                breakpoint: 1200,
                settings: {
                    centerMode: false,
                    slidesToShow: 4,
                    slidesToScroll: 2
                }
            },
            {
                breakpoint: 991,
                settings: {
                    slidesToShow: 3,
                    centerMode: false

                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2,
                    centerMode: false,
                    slidesToScroll: 1
                }
            }

        ]
    });


    var newsItem = $(".bl_news__item").length;


    if (newsItem > 4) {
        $(".bl_news").slick({
            dots: false,
            infinite: true,

            autoplay: true,
            autoplaySpeed: 2000,

            centerMode: false,
            speed: 2000,
            adaptiveHeight: true,
            slidesToShow: 4,
            slidesToScroll: 1,
            responsive: [
                {
                    breakpoint: 991,
                    settings: {
                        slidesToShow: 3
                    }
                },
                {
                    breakpoint: 768,
                    settings: {
                        slidesToShow: 2,
                        centerMode: false,
                        slidesToScroll: 2
                    }
                },
                {
                    breakpoint: 600,
                    settings: {
                        slidesToShow: 1,
                        centerMode: false,
                        slidesToScroll: 1
                    }
                },
                {
                    breakpoint: 320,
                    settings: {
                        slidesToShow: 1,
                        centerMode: false,
                        slidesToScroll: 1
                    }
                }

            ]
        });
    }


    // Ancor to top

    $(".bl_ancor").on("click", "a", function (event) {
        event.preventDefault();
        var id = $(this).attr('href'),
            top = $(id).offset().top;
        $('body,html').animate({scrollTop: top}, 500);
    });
    $(window).scroll(function () {


        var ancor = $(".bl_ancor"),
            windowHeight = $(window).height() / 2;

        if ($(this).scrollTop() > windowHeight) {

            ancor.removeClass("hidden");

        } else {
            ancor.addClass("hidden");
        }

    });


};


