window.onload = function () {

    var
        body = $("body"),
        active = "active",
        $window = $(window),

        btnLanguage = $(".btn_language__active");


    ///  sliderEffect
    function sliderEffectForButtons(button, element, duration) {
        button.click(function () {
            element.slideToggle(duration);
        });
    }

    /// Bower slider for Main Slider
    $(".sliderBlock").slick({
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
    });



    //// Bower slider for navigation

    if (body.width() > 1024) {
        $(".bl_nav").slick({
            dots: false,
            infinite: true,
            speed: 500,
            slidesToShow: 6,
            slidesToScroll: 1,
            responsive: [
                {
                    breakpoint: 1600,
                    settings: {
                        slidesToShow: 5
                    }
                },
                {
                    breakpoint: 1200,
                    settings: {
                        slidesToShow: 4

                    }
                },
                {
                    breakpoint: 991,
                    settings: {
                        slidesToShow: 3
                    }
                }
            ]
        });
    }



    /// toggle class Active
    function toggleClassActive(elem) {
        elem.on("click", function () {
            $(this).toggleClass(active);
        });
    }


    //  hideShowEffect
    function hideShowEffect(button, element) {
        button.click(function () {
            element.toggleClass("hidden");
        });
    }

    // LikeLinks buttons
    function likeLink(btn) {
        btn.on("click", function () {
            var urlLink = $(this).attr("data-href");
            if (urlLink === "") {
                alert("URL address isn't correct / шляпа");
            } else {
                window.open(urlLink);
            }
        })
    }

    likeLink($(".sliderBlock__link"));
    likeLink($(".btn_bonus__getBonus"));






    btnLanguage.on("click", function () {
        $(this).toggleClass(active);
        $(".bl_language__other").toggleClass(active);
    });


    var btnMenu = $(".btn_menu");

    toggleClassActive(btnMenu);
    sliderEffectForButtons(btnMenu, $(".bl_mainNav__full"), 500);


    var btnSymbols = $(".btn_moreSymbolInfo"),
        symbolsItem = $(".game_symbol__wrapper");

    sliderEffectForButtons(btnSymbols, symbolsItem, 500);


    var btnBonusInfo = $(".btn_bonus__info");

    btnBonusInfo.on("click", function () {

        $(this).parent(".bl_bonus").toggleClass("bl_bonus__active");
        $(this).siblings(".bl_bonus__downSide").toggleClass("hidden");

        $(this).siblings(".bl_bonus__effect").toggleClass("active");


    });


    if (body.width() <= 1024) {

        var btnShowCatrories = $(".js-btn_caterogy");

        btnShowCatrories.one("click", function () {

            $('.bl_nav__link').each(function (i) {
                if (i !== 0) $(this).clone().appendTo('.bl_mainNav__category_full').wrap('<li class="bl_mainNav__category_item" />');
            });
            // По первому клику собирираем все ссылки навигации
        });


        btnShowCatrories.on("click", function () {

            $(".bl_mainNav__category_full").slideToggle();

        });

        /// WrapperSlides in links on main page
        function wrapperSlidesInLinks() {

            $(".sliderBlock_items__itemPhoto").each(function () {
              var currentLink =  $(this).find(".sliderBlock__link").data("href");
                $(this).children().wrapAll('<a href="'+ currentLink + '" rel="nofollow" target="_self"></a>');
            })
        }wrapperSlidesInLinks();



    }





};


