window.onload = function () {

    var butttonVideo = $(".video__button");

    butttonVideo.on("click", function () {
        var currentVideo = $(this).siblings(".video__link");
        var videoHeight = parseInt(currentVideo.innerWidth() / 16 * 9);
        var firstURL = currentVideo.data("video");
        var videoIframe = '<iframe width="100%" height="' + videoHeight + '" src="https://www.youtube.com/embed/' + firstURL + '?autoplay=1" frameborder="0" allow="autoplay" allowfullscreen></iframe>';
        $(this).closest(".video").html(videoIframe);
    });


    var btnFaq = $(".bl_faq__btn");

    btnFaq.on("click", function(){
        $(this).toggleClass("active");
        $(this).next(".bl_faq__desc").slideToggle(600);
    })

};

