window.onload = function () {

    var animationTexts = document.querySelectorAll(".animation_text");
    var animationTextsLength = animationTexts.length;
    var arrTopElements = [];
    var heightWindow = window.innerHeight;

    var getTopPosition = function () {
        for (var i = 0; i < animationTextsLength; i++) {
            var elem = animationTexts[i].getBoundingClientRect().top + pageYOffset;
            arrTopElements.push(elem);
        }
        return arrTopElements;
    };

    var arr = getTopPosition();

    window.addEventListener("scroll", function () {

        var scrollTop = window.pageYOffset || document.documentElement.scrollTop;
        var fullWindow = scrollTop + heightWindow;

        arr.forEach(function(currentValue, index) {

            if(fullWindow > currentValue){
            animationTexts[index].classList.add("active-animation");
            }

        });

    });




};
